var message = {};
var time = 0;
var date = 0;
var year = 0;
var month = 0;
var day = 0;
// Get Time and convertit to hours (time[0]) and minutes (time[1])
time = new Date().toISOString();
date = time.split("T")[0];
year = date.split("-")[0];
month = date.split("-")[1];
day = date.split("-")[2];
time = time.split("T")[1].split(".")[0];
time = time.split(":");
// Adjust time to our timezone and crops it to 24h
time[0] = (parseInt(time[0]) + 8)
if (time[0] >= 25) {
    time[0] -= 24;
    day += 1
}
// Adjust time to spell "9" instead of "09" 
// and e.g. "12 Uhr" instead of "12 Uhr 0"
time[1] = parseInt(time[1])
if (time[1] === 0) {
    time[1] = "";
}
flow.set("date", year + "-" + month + "-" + day);
flow.set("time", time[0] + ":" + time[1] + ":" + time[2] + ".000+08:00");
// Create text refered to ppython datetime format "%Y-%m-%d %H:%M:%S.%f%z"
message.payload = "Date: " + year + "-" + month + "-" + day + "\nTime: "+ time[0]+":"+ time[1]+":" + time[2] +".000+08:00";
return message;

